const low = $input.first().json.chart.result[0].indicators.quote[0].low;
const volume = $input.first().json.chart.result[0].indicators.quote[0].volume;
const timestamps = $input.first().json.chart.result[0].timestamp;

const ticker = $input.first().json.chart.result[0].meta.symbol;
const longName = $input.first().json.chart.result[0].meta.longName;

// RSI (14, last value)
function calcRSI(closes, period=14) {
  if (closes.length < period+1) return null;
  let gains = 0, losses = 0;
  for (let i=1; i<=period; i++) {
    const diff = closes[closes.length - period - 1 + i] - closes[closes.length - period - 2 + i];
    if (diff > 0) gains += diff; else losses -= diff;
  }
  let avgGain = gains / period, avgLoss = losses / period;
  let rs = avgGain / (avgLoss || 1e-9);
  let rsi = 100 - (100 / (1 + rs));
  return Number(rsi.toFixed(2));
}
const rsi14 = calcRSI(close, 14);

// MACD (last value)
function EMA(arr, period) {
  const k = 2 / (period + 1);
  let emaArr = [arr[0]];
  for (let i=1; i<arr.length; i++) {
    emaArr.push(arr[i]*k + emaArr[i-1]*(1-k));
  }
  return emaArr;
}
function calcMACD(closes) {
  if (closes.length < 26) return {macd: null, signal: null};
  const ema12 = EMA(closes, 12);
  const ema26 = EMA(closes, 26);
  const macd = ema12.map((val,i) => val-(ema26[i]||0));
  const signal = EMA(macd, 9);
  return { 
    macd: Number(macd.at(-1).toFixed(2)), 
    signal: Number(signal.at(-1).toFixed(2)) 
  };
}
const macd = calcMACD(close);

// MA50, MA200 (use last 50/200 values)
function SMA(arr, period) {
  if (arr.length < period) return null;
  return arr.slice(-period).reduce((a,b)=>a+b,0)/period;
}
const ma50 = Number(SMA(close, 50)?.toFixed(2));
const ma200 = Number(SMA(close, 200)?.toFixed(2));

// MA slopes
const prev_ma50 = Number(SMA(close.slice(0,-1), 50)?.toFixed(2));
const prev_ma200 = Number(SMA(close.slice(0,-1), 200)?.toFixed(2));
const slope50 = ma50 > prev_ma50 ? "Up" : "Down";
const slope200 = ma200 > prev_ma200 ? "Up" : "Down";

// Volatility (ATR%)
function avgATRpct(highs, lows, closes) {
  let tr = [];
  for (let i=1; i<closes.length; i++) {
    const hl = highs[i]-lows[i];
    const hc = Math.abs(highs[i]-closes[i-1]);
    const lc = Math.abs(lows[i]-closes[i-1]);
    tr.push(Math.max(hl, hc, lc) / closes[i] * 100);
  }
  return Number((tr.reduce((a,b)=>a+b,0)/tr.length).toFixed(2));
}
const volatility = avgATRpct(high, low, close);

// Support/Resistance: last 252 bars for 1-year, or increase to last 260 for full year
const support = Number(Math.min(...low.slice(-252)).toFixed(2));
const resistance = Number(Math.max(...high.slice(-252)).toFixed(2));

// 52-week high/low: use last 252-260 closes for trading year
const week52high = Number(Math.max(...close.slice(-260)).toFixed(2));
const week52low = Number(Math.min(...close.slice(-260)).toFixed(2));

// Trend
let trend = "Sideways";
if (slope200 === "Up" && close.at(-1) > ma200) trend = "Up";
if (slope200 === "Down" && close.at(-1) < ma200) trend = "Down";

// Volume Trend
function SMAvol(volumes, period=20) {
  if (volumes.length < period) return null;
  return volumes.slice(-period).reduce((a,b)=>a+b,0)/period;
}
const smaVol20 = SMAvol(volume, 20);
const volTrend = volume.at(-1) > smaVol20 ? "Up" : "Down";

// Volume Spike: last 252 bars for recent year
const volSpike = volume.slice(-252).map((v,i,arr) => {
  const avg = SMAvol(arr.slice(Math.max(0,i-19), i+1), 20);
  if (avg && v >= 2 * avg) return { bar: i, volume: v, avg: avg };
  return null;
}).filter(x=>x);

// Unusual Gap: on all bars (could be thousands in 5y)
const gaps = [];
for (let i=1;i<open.length;i++) {
  const gap = Math.abs(open[i] - close[i-1]) / close[i-1];
  if (gap >= 0.01) gaps.push({ index: i, gap: Number((gap*100).toFixed(2)), date: timestamps[i] });
}

// === Bias calculation ===
let bias = "Neutral";
if (trend === "Up" && rsi14 <= 70 && macd.macd > 0) bias = "Bullish";
if (trend === "Down" && rsi14 >= 30 && macd.macd < 0) bias = "Bearish";

// === Entry price (use latest close) ===
const entryPrice = Number(close.at(-1).toFixed(2));

// === Stop loss & Take profit ===
let stopLoss = null, takeProfit = null;
if (bias === "Bullish") {
  stopLoss = Number((support * 0.99).toFixed(2));
  takeProfit = Number((resistance * 1.02).toFixed(2));
} else if (bias === "Bearish") {
  stopLoss = Number((support * 0.99).toFixed(2));
  takeProfit = Number((resistance * 0.98).toFixed(2));
} else {
  stopLoss = "N/A";
  takeProfit = "N/A";
}

return [{
  json: {
    Ticker: ticker,    
    Name: longName,
    Timeframe: "1w",
    Period: "5y",
    Description: "1w candle data for the past 5 years",
    RSI_14: rsi14,
    MACD: macd,
    MA50: ma50,
    MA50_slope: slope50,
    MA200: ma200,
    MA200_slope: slope200,
    Volatility_ATR_pct: volatility,
    Support: support,
    Resistance: resistance,
    week52high,
    week52low,
    Trend: trend,
    Volume_Trend: volTrend,
    Volume_SMA20: Math.round(smaVol20),
    Bias: bias,
    Entry_Price: entryPrice,
    Stop_Loss: stopLoss,
    Take_Profit: takeProfit,
    Vol_Spike: volSpike.length > 0 ? volSpike : "None",
    Unusual_Gap: gaps.length > 0 ? gaps : "None",
  }
}];
